import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button, Card, CardBody, CardGroup, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';

class Login extends Component {
  constructor() {
     super();
     this.handleChange = this.handleChange.bind(this);
     this.submitHandler = this.submitHandler.bind(this);
     this.state = {
       inputField: ''
     };
   }

   submitHandler(evt) {
     evt.preventDefault();
     // pass the input field value to the event handler passed
     // as a prop by the parent (App)
     this.props.handlerFromParant(this.state.inputField);

     this.setState({
       inputField: ''
     });
   }

   handleChange(event) {
     this.setState({
       inputField: event.target.value
     });
   }

  render() {
    return (

      <div className="app flex-row align-items-center">
      <div>
       <form onSubmit={this.submitHandler}>
         <input type="text"

                value={this.state.inputField}
                onChange={this.handleChange} />
         <input onClick={this.handleChange} type="submit" />
       </form>
       <h5>Visible in child:<br />{this.state.inputField}</h5>
     </div>
        <Container>
          <Row className="justify-content-center">
            <Col md="8">
              <CardGroup>
                <Card className="p-4">
                  <CardBody>
                    <Form onSubmit={this.submitHandler}>
                      <h1>Login</h1>
                      <p className="text-muted">Sign In to your account</p>
                      <InputGroup className="mb-3">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="icon-user"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="text" onChange={this.handleChange} placeholder="Username" autoComplete="username" />
                      </InputGroup>
                      <InputGroup className="mb-4">
                        <InputGroupAddon addonType="prepend">
                          <InputGroupText>
                            <i className="icon-lock"></i>
                          </InputGroupText>
                        </InputGroupAddon>
                        <Input type="password" placeholder="Password" autoComplete="current-password" />
                      </InputGroup>
                      <Row>
                        <Col xs="6">
                          <Link to="/">
                          <Button color="primary" className="px-4">Login</Button>
                          </Link>
                        </Col>
                        <Col xs="6" className="text-right">
                          <Button color="link" className="px-0">Forgot password?</Button>
                        </Col>
                      </Row>
                    </Form>
                  </CardBody>
                </Card>

              </CardGroup>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Login;
